func Test_bank_sortTrx(t *testing.T) {
	b := &bank{
		trxAggregator: map[string]*pbcodec.Transaction{
			"d": {
				Id: "d",
			},
			"a": {
				Id: "a",
			},
			"b": {
				Id: "b",
			},
			"c": {
				Id: "c",
			},
		},
	}
	b.sortTrx()
	assert.Equal(t, []*pbcodec.Transaction{
		{Id: "a"},
		{Id: "b"},
		{Id: "c"},
		{Id: "d"},
	}, b.sortedTrx)
}

